mapper.update:
update user_info
email=#{email}, nick_name=#{nickName}, join_type=#{joinType}, sex=#{sex}, password=#{password}, personal_signature=#{personalSignature}, status=#{status}, last_login_time=#{lastLoginTime}, area_name=#{areaName}, area_code=#{areaCode}, last_off_time=#{lastOffTime}, update_time=#{updateTime},
where user_id=#{userId}

mapper.select:
select * from user_info

    and user_id=#{userId}
    and email=#{email}
    and nick_name like concat('%',#{nickName},'%')
    and join_type=#{joinType}
    and sex=#{sex}
    and password=#{password}
    and personal_signature=#{personalSignature}
    status=and #{status}
    and last_login_time=#{lastLoginTime}
    and area_name=#{areaName}
    and area_code=#{areaCode}
    and last_off_time=#{lastOffTime}
    and update_time=#{updateTime}

order by update_time desc
